pragma solidity >=0.4.24 <0.6.0;

contract SBCPaymentChannel {

    event PaymentChannelChanged(address indexed sender, address indexed recipient, uint256 newValue);
    event PaymentChannelClaim(address senderAddress, uint256 amountAuthorized, bytes signature, bool signed, bool sender, bool underflow, uint256 amount);
    event PaymentChannelDeposit(uint256 amount);
    event PaymentChannelWillClose(uint256 expiration);
    event PaymentChannelClose(bool isClosed);

    address public sender;      // The account sending payments.
    address public recipient;   // The account receiving the payments.
    uint256 public withdrawn = 0;   // How much the recipient has already withdrawn.

    // How much time the recipient has to respond when the sender initiates
    // channel closure.
    uint256 public closeDuration;
    // When the payment channel closes. Initially effectively infinite.
    uint256 public expiration = 2**256-1;

    function PaymentChannel(address _recipient, uint256 _closeDuration)
        public
        payable
    {
        require(sender == address(0x0));
        require(recipient == address(0x0));
        require(withdrawn == 0);
        sender = msg.sender;
        recipient = _recipient;
        closeDuration = _closeDuration;
        emit PaymentChannelChanged(msg.sender, recipient, msg.value);
    }


    // The recipient can close the channel at any time by presenting a signed
    // amount from the sender. The recipient will be sent that amount, and the
    // remainder will go back to the sender.
    function close(uint256 amount, bytes memory signature) public {
        // this recreates the message that was signed on the client
        bytes32 message = prefixed(keccak256(abi.encodePacked(amount, this)));
        require(msg.sender == recipient);
        require(recoverSigner(message, signature) == sender);

        require(amount >= withdrawn);
        recipient.transfer(amount - withdrawn);
        emit PaymentChannelClose(true);
        selfdestruct(sender);
    }



    function startSenderClose() public {
        require(msg.sender == sender);
        expiration = now + closeDuration;
        emit PaymentChannelWillClose(expiration);
    }

    // If the timeout is reached without the recipient closing the channel, then
    // the ether is released back to the sender.
    function claimTimeout() public {
        emit PaymentChannelClose(now >= expiration);
        require(now >= expiration);
        selfdestruct(sender);
    }

    function deposit() public payable {
        require(msg.sender == sender);
        emit PaymentChannelDeposit(msg.value);
    }

    function claim(uint256 amountAuthorized, bytes memory signature) public {

        // this recreates the message that was signed on the client
        bytes32 message = prefixed(keccak256(abi.encodePacked(amountAuthorized, this)));

        require(msg.sender == recipient);

        require(recoverSigner(message, signature) == sender);

        // Make sure there's something to withdraw (guards against underflow)
        require(amountAuthorized > withdrawn);
        uint256 amountToWithdraw = amountAuthorized - withdrawn;

        withdrawn += amountToWithdraw;
        recipient.transfer(amountToWithdraw);

        emit PaymentChannelClaim(sender, amountAuthorized, signature, recoverSigner(message, signature) == sender, (msg.sender == recipient), (amountAuthorized > withdrawn), amountToWithdraw);

    }

     /// signature methods.
    function splitSignature(bytes memory sig)
        internal
        pure
        returns (uint8 v, bytes32 r, bytes32 s)
    {
        require(sig.length == 65);

        assembly {
            // first 32 bytes, after the length prefix.
            r := mload(add(sig, 32))
            // second 32 bytes.
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes).
            v := byte(0, mload(add(sig, 96)))
        }

        return (v, r, s);
    }

    function recoverSigner(bytes32 message, bytes memory sig)
        internal
        pure
        returns (address)
    {
        (uint8 v, bytes32 r, bytes32 s) = splitSignature(sig);

        return ecrecover(message, v, r, s);
    }

    /// builds a prefixed hash to mimic the behavior of eth_sign.
    function prefixed(bytes32 hash) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash));
    }   

}

